Let’s bootstrap!

n_rep &lt;- 500

set.seed(25)

duke_forest_bootstraps &lt;- map_dfr(
  seq_len(n_rep),
  function(i) {
    duke_forest |&gt;
      slice_sample(prop = 1, by = bed, replace = TRUE) |&gt;
      mutate(resample = i, .before = address)
  }
)

